import React, { useState, useEffect } from 'react';

// Report Preview Page Component - Display patient data in clean report format
const ReportPreviewPage = () => {
  const [savedPatients, setSavedPatients] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [reportData, setReportData] = useState(null);

  // Load saved patients from localStorage
  useEffect(() => {
    const saved = localStorage.getItem('savedPatients');
    if (saved) {
      try {
        setSavedPatients(JSON.parse(saved));
      } catch (error) {
        console.error('Error loading saved patients:', error);
      }
    }
  }, []);

  // Select a patient to view their report
  const selectPatient = (patient) => {
    setSelectedPatient(patient);
    setReportData(patient.data);
  };

  // Print/Download the report
  const printReport = () => {
    if (!selectedPatient) return;
    
    // Create a new window for printing
    const printWindow = window.open('', '_blank');
    const reportContent = generatePrintableReport();
    
    printWindow.document.write(`
      <!DOCTYPE html>
      <html>
        <head>
          <title>Patient Report - ${selectedPatient.id}</title>
          <style>
            body {
              font-family: Arial, sans-serif;
              margin: 20px;
              line-height: 1.6;
            }
            .report-header {
              border-bottom: 2px solid #2c3e50;
              padding-bottom: 10px;
              margin-bottom: 20px;
            }
            .report-field {
              margin-bottom: 15px;
              padding: 10px;
              border-bottom: 1px solid #ecf0f1;
            }
            .report-field-label {
              font-weight: bold;
              color: #2c3e50;
              margin-bottom: 5px;
            }
            .report-field-value {
              color: #34495e;
            }
            .report-footer {
              margin-top: 30px;
              padding-top: 10px;
              border-top: 1px solid #ddd;
              font-size: 12px;
              color: #7f8c8d;
            }
            @media print {
              body { margin: 10px; }
              .no-print { display: none; }
            }
          </style>
        </head>
        <body>
          ${reportContent}
          <div class="report-footer">
            Report generated on ${new Date().toLocaleString()}<br>
            Patient ID: ${selectedPatient.id}<br>
            Original data saved: ${new Date(selectedPatient.timestamp).toLocaleString()}
          </div>
        </body>
      </html>
    `);
    
    printWindow.document.close();
    printWindow.focus();
    
    // Wait for content to load, then print
    setTimeout(() => {
      printWindow.print();
      printWindow.close();
    }, 250);
  };

  // Generate printable report HTML
  const generatePrintableReport = () => {
    if (!selectedPatient || !reportData) return '';

    let html = `
      <div class="report-header">
        <h1>Patient Report</h1>
        <h2>Patient ID: ${selectedPatient.id}</h2>
      </div>
      <div class="report-content">
    `;

    selectedPatient.structure.forEach((component) => {
      const value = reportData[component.id] || 'Not provided';
      html += `
        <div class="report-field">
          <div class="report-field-label">${component.editableLabel}</div>
          <div class="report-field-value">${value}</div>
        </div>
      `;
    });

    html += `</div>`;
    return html;
  };

  // Delete patient record
  const deletePatient = (patientId) => {
    if (confirm('Are you sure you want to delete this patient record?')) {
      const updatedPatients = savedPatients.filter(p => p.id !== patientId);
      localStorage.setItem('savedPatients', JSON.stringify(updatedPatients));
      setSavedPatients(updatedPatients);
      
      if (selectedPatient?.id === patientId) {
        setSelectedPatient(null);
        setReportData(null);
      }
    }
  };

  // If no patients saved, show message
  if (savedPatients.length === 0) {
    return (
      <div className="report-preview">
        <div className="empty-state">
          <h3>No Patient Reports Available</h3>
          <p>Please go to the Data Entry page to add patient data first.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="report-preview">
      <h2>Patient Report Preview</h2>
      
      {/* Patient Selection */}
      <div style={{ marginBottom: '2rem' }}>
        <h3>Select Patient Report</h3>
        <div style={{ marginTop: '1rem' }}>
          {savedPatients.map((patient) => (
            <div
              key={patient.id}
              style={{
                background: '#f8f9fa',
                border: selectedPatient?.id === patient.id ? '2px solid #3498db' : '1px solid #dee2e6',
                borderRadius: '4px',
                padding: '1rem',
                marginBottom: '0.5rem',
                cursor: 'pointer',
                transition: 'all 0.3s'
              }}
              onClick={() => selectPatient(patient)}
            >
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div>
                  <strong>ID: {patient.id}</strong>
                  <br />
                  <small style={{ color: '#6c757d' }}>
                    Saved: {new Date(patient.timestamp).toLocaleString()}
                  </small>
                </div>
                <button
                  className="remove-btn"
                  style={{ padding: '0.5rem 1rem', fontSize: '0.9rem' }}
                  onClick={(e) => {
                    e.stopPropagation();
                    deletePatient(patient.id);
                  }}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Report Display */}
      {selectedPatient && reportData && (
        <div>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
            <h3>Report for Patient: {selectedPatient.id}</h3>
            <button className="btn" onClick={printReport}>
              üñ®Ô∏è Print / Download
            </button>
          </div>

          <div className="report-content">
            {/* Report Header */}
            <div className="report-header">
              <h2>Patient Report</h2>
              <p><strong>Patient ID:</strong> {selectedPatient.id}</p>
              <p><strong>Report Generated:</strong> {new Date().toLocaleString()}</p>
              <p><strong>Data Last Updated:</strong> {new Date(selectedPatient.timestamp).toLocaleString()}</p>
            </div>

            {/* Report Fields */}
            <div className="report-section">
              {selectedPatient.structure.map((component) => {
                const value = reportData[component.id] || 'Not provided';
                return (
                  <div key={component.id} className="report-field">
                    <div className="report-field-label">
                      {component.editableLabel}
                    </div>
                    <div className="report-field-value">
                      {value}
                    </div>
                  </div>
                );
              })}
            </div>

            {/* Report Footer */}
            <div className="report-footer" style={{ 
              marginTop: '2rem', 
              padding: '1rem', 
              borderTop: '1px solid #ddd',
              fontSize: '0.9rem',
              color: '#6c757d'
            }}>
              <p>This report was generated using the Patient Report Builder application.</p>
              <p>Total fields in report: {selectedPatient.structure.length}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ReportPreviewPage;
